import React from 'react';
import Card from './Card';

export default function Cards({characters}) {//¿Por que se utiliza characters como nombre del array en vez de "default"?
   return(
      <div>
         {
            characters.map(({id, name, status, species, gender, origin, image})=> {
               return(
                  <Card
                  key={id}//este id es para react
                  id={id}// este id es para el desarrollador
                  name={name}
                  status={status}
                  species={species}
                  gender={gender}
                  origin={origin.name}
                  image={image}
                  onClose={() => window.alert('Federex')}

                  />

               )

            })
         }


      </div>
   )


}
